#CONS - Consensus and Profile by Teodora Kovacevic

import re

dataset = ">Rosalind_>Rosalind_>Rosalind_>Rosalind_>Rosalind_>Rosalind_>Rosalind_>Rosalind_>Rosalind_>Rosalind_"
sequences = re.findall(r'[ATGC]+', dataset)
names = re.findall(r'Rosalind_[0-9]+',dataset)

A_cont = []
T_cont = []
G_cont = []
C_cont = []

for i in range(len(sequences[0])):
    A_cont.append(0)
    T_cont.append(0)
    G_cont.append(0)
    C_cont.append(0)

for i in range(0,len(sequences)):
    for j in range(0,len(sequences[0])):
        if sequences[i][j] == "A":
            A_cont[j]+=1
        elif sequences[i][j] == "G":
            G_cont[j]+=1
        elif sequences[i][j] == "C":
            C_cont[j]+=1
        elif sequences[i][j] == "T":
            T_cont[j]+=1

max_num = A_cont.copy()
max_content =[]

for h in range(len(A_cont)):
    max_content.append("A")


for l in range(0,len(A_cont)):

    if max_num[l]<T_cont[l]:
        max_content[l]="T"
        max_num[l]=T_cont[l]

    if max_num[l]<C_cont[l]:
        max_content[l]="C"
        max_num[l]=C_cont[l]

    if max_num[l]<G_cont[l]:
        max_content[l]="G"
        max_num[l]=G_cont[l]

    
def string_convert(s): 
    DNA_string = "" 
    for x in s: 
        DNA_string += x 
    return DNA_string

print(string_convert(max_content))
    
print("A:", *A_cont)
print("T:", *T_cont)
print("G:", *G_cont)
print("C:", *C_cont)

