#GC - Computing GC Content by Teodora Kovacevic

import re

dataset = ">Rosalind_>Rosalind_>Rosalind_>Rosalind_>Rosalind_>Rosalind_"
sequences = re.findall(r'[ATGC]+', dataset)
names = re.findall(r'Rosalind_[0-9]+',dataset)
contents = []
gc = 0
gc_content = 0
for i in sequences:
    for k in i:
        if k =="G" or k =="C":
            gc+=1
    gc_content = gc/len(i)
    contents.append(gc_content)
    gc_content, gc = 0,0
index_rosalind = contents.index(max(contents))
print (names[index_rosalind])
print(max(contents)*100)
